var MoratabRules = {
	tags: {
		h1: true,
		h2: true,
		h3: true,
		b: true,
		i: true,
		br: true,
		p: true,
		div: true,
		span: true,
		ul: true,
		ol: true,
		li: true,
		code: true,
		em: 'b',
		strong: 'b',
		a: {
			check_attributes: {
				href: 'url'
			}
		},
	}
};


var reMarker = new reMarked({
	h1_setext: false,
	h2_setext: false,
	li_bullet: '+',
	indnt_str: '\t',
	nbsp_spc: true,
	span_tags: false,
	div_tags: false,
});


var htmlToMoratab = function(html) {
	refined = wysihtml5.dom.parse(html, MoratabRules).replace(/\n/g, ' ');
	return reMarker.render(refined);
}
